import React, { Component } from 'react';
import Projects from './Components/Projects';
import AddProject from './Components/AddProject';
import uuid from 'uuid';
import { Card, CardImg, CardTitle, CardSubtitle, CardBody, CardText, Button, Jumbotron, Alert, Fade } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

//import {Container, Row, Col } from 'reactstrap';
import './App.css';

class App extends Component {

  constructor(){
    super();
    this.state = {
      projects:[]
    }
  }

  componentDidMount(){
    this.setState({projects: [
        {
          id:uuid.v4(),
          title: 'Business Website',
          category: 'Web Design'
        },
        {
          id:uuid.v4(),
          title: 'Social App',
          category: 'Mobile Development'
        },
        {
          id:uuid.v4(),
          title: 'Ecommerce Shopping Cart',
          category: 'Web Development'
        }
    ]});
  }

  handleAddProject(project){
    let projects = this.state.projects;
    projects.push(project);
    this.setState({projects:projects});
  }

  handleDeleteProject(id){
    let projects = this.state.projects;
    let index = projects.findIndex(x => x.id === id);
    projects.splice(index, 1)
    this.setState({projects:projects});
  }

  render(){
    return(
      <div className="App">
        <Jumbotron>
          <h1 className="display-5">Add Users</h1>
          <p className="lead">This is a simple hello unit, a simple Jumbotron-style component for calling extra attention to feature content or information.</p><hr />
          <p>It uses utility classes for typography and spacing to space content out within the larger container.</p>
          <p className="lead">
            <Button onClick={this.clicky} color="primary" outline block={false}>Learn More</Button>
          </p>
        </Jumbotron>

        <Card>
          <CardImg color="primary" top width="100%" src="https://placeholdit.imgix.net/~text? txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap" />
          <hr />
          <CardBody>
            <CardTitle >Card Title</CardTitle>
            <CardSubtitle>Card Subtitle</CardSubtitle>
          </CardBody>
        </Card>
        <AddProject  addProject={this.handleAddProject.bind(this)} />
        <Projects projects={this.state.projects} onDelete={this.handleDeleteProject.bind(this)} />

      </div>
    )
  }
}


export default App;
